import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';

const Image = ({ imgSrc }) => {
    const [imgIsOk, setIsOk] = useState(true);

    const handleError = () => {
        setIsOk(false);
    }

    return (
        <img onError={handleError} src={imgIsOk ? imgSrc : "data:image/svg+xml;base64,"} />
    );
}

export default Image;